import { IExpressController } from '../types/express';
import bankingServices from '../services/bankingServices';
import bankingErrors from '../errors/bankingErrors';
import { StatusCodes } from 'http-status-codes';

const deposit: IExpressController = async (req, res, next) => {
  try {
    //getting request data
    const { quantity } = req.body;
    const token = req.headers.authorization;

    //check token existence
    if (!token) throw bankingErrors.missingToken;

    //making deposit to the db
    const { account } = await bankingServices.deposit({
      quantity,
      token,
    });

    return res.status(StatusCodes.OK).json({
      message: `Successfully deposited R$${quantity} on account #${account}`,
    });
  } catch (err) {
    next(err);
  }
};

const transfer: IExpressController = async (req, res, next) => {
  try {
    const { quantity, transferAccount } = req.body;
    const token = req.headers.authorization;

    //check token existence
    if (!token) throw bankingErrors.missingToken;

    //make transfer
    const { senderAccount } = await bankingServices.transfer({
      transferAccount,
      quantity,
      token,
    });

    return res.status(StatusCodes.OK).json({
      message: `Account #${senderAccount} successfully transferred R$${quantity} to account #${transferAccount}`,
    });
  } catch (err) {
    next(err);
  }
};

export default { deposit, transfer };

import { IDepositReq, ITransferReq } from '../types/transfer';
import bankingErrors from '../errors/bankingErrors';

import bankingModels from '../models/bankingModels';
import jwtToken from '../auth/token';

const validateQuantity = (quantity: number) => {
  if (quantity > 2000 || quantity < 0 || typeof quantity === 'string')
    throw bankingErrors.invalidQuantity;
};

const deposit = async ({ quantity, token }: IDepositReq) => {
  //validate token
  const { data: user } = jwtToken.validateToken(token);

  //validate quantity
  validateQuantity(quantity);

  //create query to update wallet value
  const filter = { account: user.account };
  const query = { $inc: { wallet: quantity } };
  const updateResponse = await bankingModels.updateOne(filter, query);

  //create object to respond the controller
  const depositResponse = {
    done: updateResponse.acknowledged,
    account: user.account,
  };
  return depositResponse;
};

const transfer = async ({ transferAccount, quantity, token }: ITransferReq) => {
  //validate token
  const { data: sender } = jwtToken.validateToken(token);

  //validate quantity
  validateQuantity(quantity);

  //validate sender has enough in wallet
  const senderData = await bankingModels.findByAccount(sender.account);
  if (senderData?.wallet < quantity) throw bankingErrors.notEnoughTransfer;

  // create query to update wallet value
  const filterReceiver = { account: transferAccount };
  const queryReceiver = { $inc: { wallet: quantity } };
  const updateReceiverResponse = await bankingModels.updateOne(
    filterReceiver,
    queryReceiver
  );

  //create query to update sender wallet value
  const filterSender = { account: sender.account };
  const querySender = { $inc: { wallet: -quantity } };
  const updateSenderResponse = await bankingModels.updateOne(
    filterSender,
    querySender
  );

  return { senderAccount: senderData?.account };
};

export default { deposit, transfer };

import { StatusCodes } from 'http-status-codes';

const bankingErrors = {
  missingToken: {
    status: StatusCodes.BAD_REQUEST,
    message: 'Missing token on the header',
  },
  invalidFormat: {
    status: StatusCodes.BAD_REQUEST,
    message:
      'Invalid format. Account is a string of 5 digits and password is a string of 6 digits.',
  },
  invalidQuantity: {
    status: StatusCodes.BAD_REQUEST,
    message:
      'Invalid quantity, amount need to be positive number and smaller then R$2000.',
  },
  notEnoughTransfer: {
    status: StatusCodes.BAD_REQUEST,
    message:
      'Your account do not have enough money on wallet to make the transference.',
  },
  invalidToken:{
    status: StatusCodes.BAD_REQUEST,
    message:
      'Invalid token. You need to be log in to receive a valid token.',
  }
};

export default bankingErrors;

import { Document, Filter } from 'mongodb';
import { connectToDatabase } from './connection';
import mongoModels from './mongoModels';

const findByAccount = async (account: string) => {
  const foundResponse = await mongoModels.findOne({ account }, 'user');
  return foundResponse;
};

const updateOne = async (filter: Filter<Document>, query: Filter<Document>) => {  
  const updateResponse = await mongoModels.updateOne(filter, query, 'user');
  return updateResponse;
};

export default { findByAccount, updateOne };

import { Document, Filter } from 'mongodb';
import { connectToDatabase } from './connection';

const findOne = async (query: Filter<Document>, collection: string) => {
  const conn = await connectToDatabase();
  const coll = conn.collection(collection);
  const findResult = coll.findOne(query);
  return findResult;
};

const updateOne = async (
  filter: Filter<Document>,
  query: Filter<Document>,
  collection: string
) => {
  const conn = await connectToDatabase();
  
  const coll = conn.collection(collection);
  const updateResult = coll.updateOne(filter, query);
  return updateResult;
};

export default { findOne, updateOne };

import { Db, MongoClient } from 'mongodb';
require('dotenv').config();

const { MONGO_DB_STRING, MONGO_DB_NAME } = process.env;

let db: Db | null = null;

export async function connectToDatabase() {
  return db
    ? Promise.resolve(db)
    : MongoClient.connect(MONGO_DB_STRING as string).then((conn) => {
        db = conn.db(`${MONGO_DB_NAME}`);
        return db;
      });
}

import { Algorithm, JwtPayload, sign, verify } from 'jsonwebtoken';
import bankingErrors from '../errors/bankingErrors';
import { ILoginReq } from '../types/user';

const jwtConfig = {
  expiresIn: '7d',
  algorithm: 'HS256' as Algorithm,
};
const JWT_SECRET = process.env.JWT_SECRET || 'super_duper_secret';

export const generateToken = (user: ILoginReq) => {
  return sign({ data: user }, JWT_SECRET, jwtConfig);
};

export const validateToken = (token: string) => {
  try{
    const data = verify(token, JWT_SECRET) as JwtPayload;
    return { ...data };
  } catch (err){
    throw bankingErrors.invalidToken;
  }

};

export default {
  generateToken,
  validateToken,
};

import { IExpressController } from '../types/express';
import userServices from '../services/userServices';
import { StatusCodes } from 'http-status-codes';

const create: IExpressController = async (req, res, next) => {
  try {
    const user = req.body;
    const createResponse = await userServices.create(user);
    
    const response = { message: 'User created successfully', user }    
    
    if (createResponse)
      return res
        .status(StatusCodes.CREATED)
        .json(response);
  } catch (err) {    
    next(err);
  }
};

const login: IExpressController = async (req, res, next) => {
  try{
    const {account, password} = req.body;
    const loginResponse = await userServices.login({account, password});
    return res.status(StatusCodes.OK).json({token:loginResponse})
  } catch (err) {
    next(err);
  }
}

export default { create, login };

import userModels from '../models/userModels';
import { ILoginReq, IUserReq } from '../types/user';
import { StatusCodes } from 'http-status-codes';
import userErrors from '../errors/userErrors';
import { loginAuth, userAuth } from '../lib/inputAuth';
import jwtToken from '../auth/token';

const initialWallet = 0;

const getAccountNumber = async () => {
  const mongoNumberFind = await userModels.findAndUpdateCounter();
  const absoluteNumber = mongoNumberFind.value?.counter;
  const maskedNumber = `00000${absoluteNumber}`.slice(-5);

  return maskedNumber;
};

const getCompleteUser = async (user: IUserReq) => {
  const accountNumber = await getAccountNumber();
  return {
    account: accountNumber,
    ...user,
    wallet: initialWallet,
  };
};

const create = async (user: IUserReq) => {
  const userValidate = userAuth(user);

  if (userValidate.error) throw userErrors.invalidInput;

  const userExists = await userModels.findByCpf(user.cpf);
  if (userExists)
    throw userErrors.alreadyRegistered;

  const completeUser = await getCompleteUser(user);
  await userModels.createUser(completeUser);
  return true;
};

const login = async (user: ILoginReq) => {
  //validate entries
  const loginValidate = loginAuth(user);
  if (loginValidate.error) throw userErrors.invalidFormat;

  //validate user exists in the db
  const foundUser = await userModels.findByAccount(user.account);
  if (!foundUser || foundUser.password !== user.password) {
    throw userErrors.wrongCredentials;
  }

  //get the user token
  const token = jwtToken.generateToken(user);
  return token;
};

export default { create, login };

import { Document } from 'mongodb';
import { connectToDatabase } from './connection';
import mongoModels from './mongoModels';

const createUser = async (user: any) => {
  const conn = await connectToDatabase();
  const coll = conn.collection('user');
  const createResult = await coll.insertOne(user);
  return createResult;
};

const findByCpf = async (cpf: string) => {
  const conn = await connectToDatabase();
  const coll = conn.collection('user');
  const findResult = await coll.find({cpf});
  const findArray = await findResult.toArray();

  if (findArray.length === 0) return false;
  return true;
};

const findAndUpdateCounter = async () => {
  const conn = await connectToDatabase();
  const coll = conn.collection('accountCounter');
  const counterResult = await coll.findOneAndUpdate(
    {},
    { $inc: { counter: 1 } },
    { returnDocument: 'after', upsert: true }
  );
  
  return counterResult;
};

interface IFindAccount {
  (account: string) : Document | null;
}

const findByAccount:IFindAccount = async (account) => {
  const findResult = await mongoModels.findOne({account},'user');
  return findResult;
}

export default { createUser, findByCpf,findAndUpdateCounter,findByAccount };

import { StatusCodes } from 'http-status-codes';

const userErrors = {
  wrongCredentials: {
    status: StatusCodes.UNAUTHORIZED,
    message: 'Incorrect or not existent account and password',
  },
  invalidFormat: {
    status: StatusCodes.BAD_REQUEST,
    message:
      'Invalid format. Account is a string of 5 digits and password is a string of 6 digits.',
  },
  invalidInput: {
    status: StatusCodes.EXPECTATION_FAILED,
    message: 'Name or cpf invalid',
  },
  alreadyRegistered: {
    status: StatusCodes.EXPECTATION_FAILED,
    message: 'Cpf already have an account',
  },
};

export default userErrors;

import Joi from 'joi';
import { ILoginReq, IUserReq } from '../types/user';

const loginSchema = Joi.object({
  account: Joi.string().length(5).required(),
  password: Joi.string().length(6).required(),
});

const userSchema = Joi.object({
  name: Joi.string().required(),
  cpf: Joi.string().min(11).max(14).required(),
  password: Joi.string().length(6).required(),
});


export const loginAuth = (user:ILoginReq) => {
  return loginSchema.validate(user);
};

export const userAuth = (user:IUserReq) => {
  return userSchema.validate(user);
}

import { ConnectOptions, MongoClient } from 'mongodb';
import { MongoMemoryServer } from 'mongodb-memory-server';

const mongo = MongoMemoryServer.create();

const getConnection = async () => {
  const mockUri = (await mongo).getUri();
  const options = {
    useNewUrlParser: true,
    useUnifiedTopology: true,
  };

  return MongoClient.connect(mockUri, options as ConnectOptions);
};

export default getConnection;
